    String palavra;
    long tempo;

    public Desafio(String palavra, long tempo) {
        this.palavra = palavra;
        this.tempo = tempo;
    }
            public void run() {
        try {
            for (int i = 0; i < 100; i++) {
                System.out.println(palavra);
                Thread.sleep(tempo);
            }
        } catch (InterruptedException ex) {
           return ;
        }
    }

